create table sch_course (
    id            number
        generated by default on null as identity minvalue 1 maxvalue 9999999999999999999999999999 increment by 1 start with 1 cache 20
        noorder nocycle nokeep noscale
    not null enable,
    department_id number,
    title         varchar2(150 char) not null enable,
    description   varchar2(500 char),
    credits       number,
    created       date not null enable,
    created_by    varchar2(255 char) not null enable,
    updated       date not null enable,
    updated_by    varchar2(255 char) not null enable,
    auditable_yn  varchar2(1 byte) default 'N'
);

alter table sch_course
    add constraint sch_course_credits_bet check ( credits between 1 and 5 ) enable;

alter table sch_course
    add constraint sch_course_id_pk primary key ( id )
        using index enable;

