create table sch_course_instructor (
    id            number
        generated by default on null as identity minvalue 1 maxvalue 9999999999999999999999999999 increment by 1 start with 1 cache 20
        noorder nocycle nokeep noscale
    not null enable,
    course_id     number,
    instructor_id number,
    is_primary    varchar2(1 char)
);

alter table sch_course_instructor
    add constraint sch_course_instructor_id_pk primary key ( id )
        using index enable;

alter table sch_course_instructor
    add constraint sch_course_instructor_is_primary
        check ( is_primary in ( 'Y', 'N' ) ) enable;

alter table sch_course_instructor
    add constraint sch_course_instructor_is_primary_ck
        check ( is_primary in ( 'Y', 'N' ) ) enable;


